import 'package:flutter/material.dart';import 'package:flutter_app/dashboard.dart';import 'package:flutter_app/profile.dart';class Home extends StatefulWidget {  //const Home({Key? key}) : super(key: key);  @override  _HomeState createState() => _HomeState();}class _HomeState extends State<Home> {  int currentTab = 0;  final List<Widget> screens = [    Dashboard(),    Profile(),  ];  final PageStorageBucket bucket = PageStorageBucket();  Widget currentScreen = Dashboard();  @override  Widget build(BuildContext context) {    return Scaffold(      body: PageStorage(        child: currentScreen,          bucket: bucket,      ),      floatingActionButton: FloatingActionButton(        child: Icon(Icons.qr_code),        onPressed: (){},      ),      floatingActionButtonLocation: FloatingActionButtonLocation.centerDocked,      bottomNavigationBar: BottomAppBar(        shape:  CircularNotchedRectangle(),        notchMargin: 10,        child: Container(          height: 60,            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: <Widget>[                Row(                  crossAxisAlignment: CrossAxisAlignment.start,                  children: [                    MaterialButton(                      minWidth: 180,                        onPressed: (){                        setState(() {                          currentScreen = Dashboard();                          currentTab = 0;                        });                        },                      child: Column(                        mainAxisAlignment: MainAxisAlignment.center,                        children: [                          Icon(                            Icons.home,                            color: currentTab == 0 ? Colors.blue : Colors.grey,                          ),                          // Text(                          //   'Профиль',                          //   style:TextStyle(color: currentTab == 0 ? Colors                          //       .blue : Colors.grey),                          // )                        ],                      ),                    ),                    MaterialButton(                      minWidth: 180,                      onPressed: (){                        setState(() {                          currentScreen = Profile();                          currentTab = 1;                        });                      },                      child: Column(                        mainAxisAlignment: MainAxisAlignment.center,                        children: [                          Icon(                            Icons.person,                            color: currentTab == 1 ? Colors.blue : Colors.grey,                          ),                          // Text(                          //   'Главная страница',                          //   style:TextStyle(color: currentTab == 1 ? Colors                          //       .blue : Colors.grey),                          // )                        ],                      ),                    )                  ],                )              ],            ),        ),      ),    );  }}